&lt;p&gt;executeBatchSerially核心源码如下：&lt;/p&gt;
&lt;figure class=&#34;highlight java&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;3&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;4&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;5&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;6&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;7&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;8&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;9&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;10&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;11&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;12&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;13&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;14&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;15&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;16&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;17&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;18&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;19&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;20&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;21&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;22&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;23&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;24&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;25&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;26&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;27&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;28&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;29&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;30&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;31&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;32&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;33&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;code class=&#34;hljs java&#34;&gt;&lt;span class=&#34;hljs-keyword&#34;&gt;for&lt;/span&gt; (batchCommandIndex = &lt;span class=&#34;hljs-number&#34;&gt;0&lt;/span&gt;; batchCommandIndex &amp;lt; nbrCommands; batchCommandIndex++) &amp;#123;&lt;br&gt;&lt;br&gt;    ((PreparedQuery&amp;lt;?&amp;gt;) &lt;span class=&#34;hljs-built_in&#34;&gt;this&lt;/span&gt;.query).setBatchCommandIndex(batchCommandIndex);&lt;br&gt;&lt;br&gt;    &lt;span class=&#34;hljs-type&#34;&gt;Object&lt;/span&gt; &lt;span class=&#34;hljs-variable&#34;&gt;arg&lt;/span&gt; &lt;span class=&#34;hljs-operator&#34;&gt;=&lt;/span&gt; &lt;span class=&#34;hljs-built_in&#34;&gt;this&lt;/span&gt;.query.getBatchedArgs().get(batchCommandIndex);&lt;br&gt;&lt;br&gt;    &lt;span class=&#34;hljs-keyword&#34;&gt;try&lt;/span&gt; &amp;#123;&lt;br&gt;        &lt;span class=&#34;hljs-keyword&#34;&gt;if&lt;/span&gt; (arg &lt;span class=&#34;hljs-keyword&#34;&gt;instanceof&lt;/span&gt; String) &amp;#123;&lt;br&gt;            updateCounts[batchCommandIndex] = executeUpdateInternal((String) arg, &lt;span class=&#34;hljs-literal&#34;&gt;true&lt;/span&gt;, &lt;span class=&#34;hljs-built_in&#34;&gt;this&lt;/span&gt;.retrieveGeneratedKeys);&lt;br&gt;&lt;br&gt;            &lt;span class=&#34;hljs-comment&#34;&gt;// limit one generated key per OnDuplicateKey statement&lt;/span&gt;&lt;br&gt;            getBatchedGeneratedKeys(&lt;span class=&#34;hljs-built_in&#34;&gt;this&lt;/span&gt;.results.getFirstCharOfQuery() == &lt;span class=&#34;hljs-string&#34;&gt;&amp;#x27;I&amp;#x27;&lt;/span&gt; &amp;amp;&amp;amp; containsOnDuplicateKeyInString((String) arg) ? &lt;span class=&#34;hljs-number&#34;&gt;1&lt;/span&gt; : &lt;span class=&#34;hljs-number&#34;&gt;0&lt;/span&gt;);&lt;br&gt;        &amp;#125; &lt;span class=&#34;hljs-keyword&#34;&gt;else&lt;/span&gt; &amp;#123;&lt;br&gt;            QueryBindings&amp;lt;?&amp;gt; queryBindings = (QueryBindings&amp;lt;?&amp;gt;) arg;&lt;br&gt;            updateCounts[batchCommandIndex] = executeUpdateInternal(queryBindings, &lt;span class=&#34;hljs-literal&#34;&gt;true&lt;/span&gt;);&lt;br&gt;&lt;br&gt;            &lt;span class=&#34;hljs-comment&#34;&gt;// limit one generated key per OnDuplicateKey statement&lt;/span&gt;&lt;br&gt;            getBatchedGeneratedKeys(containsOnDuplicateKeyUpdateInSQL() ? &lt;span class=&#34;hljs-number&#34;&gt;1&lt;/span&gt; : &lt;span class=&#34;hljs-number&#34;&gt;0&lt;/span&gt;);&lt;br&gt;        &amp;#125;&lt;br&gt;    &amp;#125; &lt;span class=&#34;hljs-keyword&#34;&gt;catch&lt;/span&gt; (SQLException ex) &amp;#123;&lt;br&gt;        updateCounts[batchCommandIndex] = EXECUTE_FAILED;&lt;br&gt;&lt;br&gt;        &lt;span class=&#34;hljs-keyword&#34;&gt;if&lt;/span&gt; (&lt;span class=&#34;hljs-built_in&#34;&gt;this&lt;/span&gt;.continueBatchOnError &amp;amp;&amp;amp; !(ex &lt;span class=&#34;hljs-keyword&#34;&gt;instanceof&lt;/span&gt; MySQLTimeoutException) &amp;amp;&amp;amp; !(ex &lt;span class=&#34;hljs-keyword&#34;&gt;instanceof&lt;/span&gt; MySQLStatementCancelledException)&lt;br&gt;                &amp;amp;&amp;amp; !hasDeadlockOrTimeoutRolledBackTx(ex)) &amp;#123;&lt;br&gt;            sqlEx = ex;&lt;br&gt;        &amp;#125; &lt;span class=&#34;hljs-keyword&#34;&gt;else&lt;/span&gt; &amp;#123;&lt;br&gt;            &lt;span class=&#34;hljs-type&#34;&gt;long&lt;/span&gt;[] newUpdateCounts = &lt;span class=&#34;hljs-keyword&#34;&gt;new&lt;/span&gt; &lt;span class=&#34;hljs-title class_&#34;&gt;long&lt;/span&gt;[batchCommandIndex];&lt;br&gt;            System.arraycopy(updateCounts, &lt;span class=&#34;hljs-number&#34;&gt;0&lt;/span&gt;, newUpdateCounts, &lt;span class=&#34;hljs-number&#34;&gt;0&lt;/span&gt;, batchCommandIndex);&lt;br&gt;&lt;br&gt;            &lt;span class=&#34;hljs-keyword&#34;&gt;throw&lt;/span&gt; SQLError.createBatchUpdateException(ex, newUpdateCounts, &lt;span class=&#34;hljs-built_in&#34;&gt;this&lt;/span&gt;.exceptionInterceptor);&lt;br&gt;        &amp;#125;&lt;br&gt;    &amp;#125;&lt;br&gt;&amp;#125;&lt;br&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;

&lt;p&gt;通过代码分析，也确实是一条一条SQL执行，而不是把batch的SQL发送到服务器&lt;/p&gt;
&lt;p&gt;&lt;strong&gt;但是&lt;/strong&gt;&lt;/p&gt;
&lt;p&gt;重点来了，执行executeBatchSerially是有条件的，再次贴一下源码：&lt;/p&gt;
&lt;figure class=&#34;highlight java&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;3&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;4&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;code class=&#34;hljs java&#34;&gt;&lt;span class=&#34;hljs-keyword&#34;&gt;if&lt;/span&gt; (!&lt;span class=&#34;hljs-built_in&#34;&gt;this&lt;/span&gt;.batchHasPlainStatements &amp;amp;&amp;amp; &lt;span class=&#34;hljs-built_in&#34;&gt;this&lt;/span&gt;.connection.getRewriteBatchedStatements()) &amp;#123;&lt;br&gt;    ...&lt;br&gt;&amp;#125;&lt;br&gt;&lt;span class=&#34;hljs-keyword&#34;&gt;return&lt;/span&gt; executeBatchSerially(batchTimeout);&lt;br&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;

&lt;p&gt;也就是说，如果没做任何配置，默认情况下if条件是进不去的，会直接执行if块后边的语句的，那么if判断了什么，判断如下：&lt;/p&gt;
&lt;figure class=&#34;highlight java&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;code class=&#34;hljs java&#34;&gt;!``&lt;span class=&#34;hljs-built_in&#34;&gt;this&lt;/span&gt;``.batchHasPlainStatements &amp;amp;&amp;amp; ``&lt;span class=&#34;hljs-built_in&#34;&gt;this&lt;/span&gt;``.connection.getRewriteBatchedStatements()&lt;br&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;

&lt;p&gt;&lt;code&gt;batchHasPlainStatements&lt;/code&gt;默认初始化就是&lt;strong&gt;false&lt;/strong&gt;，可以不用管，重点是&lt;code&gt;connection.getRewriteBatchedStatements()&lt;/code&gt;。这个是Connection的一个参数&lt;code&gt;rewriteBatchedStatements&lt;/code&gt;，会在读取&lt;code&gt;jdbcUrl&lt;/code&gt;的时候读取进来：&lt;code&gt;jdbc:mysql://localhost:3306/test?useUnicode=true&amp;amp;characterEncoding=UTF-8&amp;amp;autoReconnect=true&lt;/code&gt;&lt;/p&gt;
&lt;p&gt;我们将jdbcUrl添加一个**&lt;code&gt;rewriteBatchedStatements&lt;/code&gt;**试试，即变成：&lt;code&gt;jdbc:mysql://localhost:3306/test?useUnicode=true&amp;amp;characterEncoding=UTF-8&amp;amp;autoReconnect=true&amp;amp;***rewriteBatchedStatements=true&lt;/code&gt;&lt;/p&gt;
&lt;p&gt;这时候就会进if块了。进入if块之后，再根据执行的是insert 还是update、 delete，会走不同方法&lt;/p&gt;
&lt;ul&gt;
&lt;li&gt;如果是insert语句，满成条件情况下，会整合成形如：”&lt;strong&gt;insert into xxx_table values (xx),(yy),(zz)…&lt;/strong&gt;“这样的语句&lt;/li&gt;
&lt;li&gt;如果是update\delete语句，满成条件情况下，会整合成形如：”&lt;strong&gt;update t set … where id &amp;#x3D; 1; update t set … where id &amp;#x3D; 2; update t set … where id &amp;#x3D; 3 …&lt;/strong&gt;“这样的语句&lt;/li&gt;
&lt;/ul&gt;
&lt;p&gt;然后分批次发送给MySQL(会有一次发送的package大小限制，所以需要拆分批次),拆分如下：&lt;/p&gt;
&lt;figure class=&#34;highlight java&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;code class=&#34;hljs java&#34;&gt;&lt;span class=&#34;hljs-type&#34;&gt;int&lt;/span&gt;` `maxAllowedPacket = ``&lt;span class=&#34;hljs-built_in&#34;&gt;this&lt;/span&gt;``.connection.getMaxAllowedPacket();&lt;br&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;

&lt;figure class=&#34;highlight java&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;3&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;4&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;5&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;6&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;7&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;8&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;9&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;10&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;11&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;12&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;13&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;14&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;15&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;16&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;code class=&#34;hljs java&#34;&gt;&lt;span class=&#34;hljs-keyword&#34;&gt;if&lt;/span&gt; (!&lt;span class=&#34;hljs-built_in&#34;&gt;this&lt;/span&gt;.batchHasPlainStatements &amp;amp;&amp;amp; &lt;span class=&#34;hljs-built_in&#34;&gt;this&lt;/span&gt;.connection.getRewriteBatchedStatements()) &amp;#123;&lt;br&gt;    &lt;span class=&#34;hljs-comment&#34;&gt;//insert会在这里进行再次判断&lt;/span&gt;&lt;br&gt;    &lt;span class=&#34;hljs-keyword&#34;&gt;if&lt;/span&gt; (canRewriteAsMultiValueInsertAtSqlLevel()) &amp;#123;&lt;br&gt;        &lt;span class=&#34;hljs-keyword&#34;&gt;return&lt;/span&gt; executeBatchedInserts(batchTimeout);&lt;br&gt;    &amp;#125;&lt;br&gt;    &lt;span class=&#34;hljs-comment&#34;&gt;//update、delete会在这里进行再次判断&lt;/span&gt;&lt;br&gt;    &lt;span class=&#34;hljs-comment&#34;&gt;//1. mysql版本&amp;gt;=4.1.0&lt;/span&gt;&lt;br&gt;    &lt;span class=&#34;hljs-comment&#34;&gt;//2. batchHasPlainStatements为false&lt;/span&gt;&lt;br&gt;    &lt;span class=&#34;hljs-comment&#34;&gt;//3. batch的数量&amp;gt;3&lt;/span&gt;&lt;br&gt;    &lt;span class=&#34;hljs-keyword&#34;&gt;if&lt;/span&gt; (&lt;span class=&#34;hljs-built_in&#34;&gt;this&lt;/span&gt;.connection.versionMeetsMinimum(&lt;span class=&#34;hljs-number&#34;&gt;4&lt;/span&gt;, &lt;span class=&#34;hljs-number&#34;&gt;1&lt;/span&gt;, &lt;span class=&#34;hljs-number&#34;&gt;0&lt;/span&gt;) &amp;amp;&amp;amp; !&lt;span class=&#34;hljs-built_in&#34;&gt;this&lt;/span&gt;.batchHasPlainStatements &amp;amp;&amp;amp; &lt;span class=&#34;hljs-built_in&#34;&gt;this&lt;/span&gt;.batchedArgs != &lt;span class=&#34;hljs-literal&#34;&gt;null&lt;/span&gt;&lt;br&gt;            &amp;amp;&amp;amp; &lt;span class=&#34;hljs-built_in&#34;&gt;this&lt;/span&gt;.batchedArgs.size() &amp;gt; &lt;span class=&#34;hljs-number&#34;&gt;3&lt;/span&gt; &lt;span class=&#34;hljs-comment&#34;&gt;/* cost of option setting rt-wise */&lt;/span&gt;) &amp;#123;&lt;br&gt;        &lt;span class=&#34;hljs-keyword&#34;&gt;return&lt;/span&gt; executePreparedBatchAsMultiStatement(batchTimeout);&lt;br&gt;    &amp;#125;&lt;br&gt;&amp;#125;&lt;br&gt; &lt;br&gt;&lt;span class=&#34;hljs-keyword&#34;&gt;return&lt;/span&gt; executeBatchSerially(batchTimeout);&lt;br&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;

&lt;h1 id=&#34;总结&#34;&gt;&lt;a href=&#34;#总结&#34; class=&#34;headerlink&#34; title=&#34;总结&#34;&gt;&lt;/a&gt;总结&lt;/h1&gt;&lt;p&gt;OK，知其然，知其所以然，我们大概知晓了参数的功能及意义，那么我们总结一下。&lt;/p&gt;
&lt;p&gt;如果想要达到MySQL真正batchUpdate效果，需要有以下几个条件：&lt;/p&gt;
&lt;ul&gt;
&lt;li&gt;需要在jdbcUrl后添加参数rewriteBatchedStatements&amp;#x3D;true&lt;/li&gt;
&lt;li&gt;this.batchHasPlainStatements 为false&lt;/li&gt;
&lt;li&gt;如果是update \ delete 语句，还需要mysql版本&amp;gt;&amp;#x3D;4.1.0，并且batch的数量&amp;gt;3&lt;/li&gt;
&lt;/ul&gt;
&lt;p&gt;因此，如果可能的情况下，请在jdbcUrl后添加参数rewriteBatchedStatements&amp;#x3D;true，尽可能利用上MySQL给我们提供的便利，提高性能。&lt;/p&gt;
&lt;blockquote&gt;
&lt;p&gt;结尾ENDING:还是着重强调一下SQL方言为MySQL，这里只是针对MySQL的性能细究，请注意一下数据库！！！&lt;/p&gt;
&lt;/blockquote&gt;

 &lt;h1 id=&#34;前言&#34;&gt;&lt;a href=&#34;#前言&#34; class=&#34;headerlink&#34; title=&#34;前言&#34;&gt;&lt;/a&gt;前言&lt;/h1&gt;&lt;blockquote&gt;
&lt;p&gt;博主mybatis源码学习相关资料：&lt;/p&gt;
&lt;blockquote&gt;
&lt;p&gt;MyBatis技术内幕&lt;/p&gt;
&lt;/blockquote&gt;
&lt;p&gt;myBatis网站：&lt;/p&gt;
&lt;blockquote&gt;
&lt;p&gt;&lt;a href=&#34;https://mybatis.org/mybatis-3/zh/getting-started.html&#34;&gt;mybatis3官网文档地址&lt;/a&gt;&lt;/p&gt;
&lt;/blockquote&gt;
&lt;/blockquote&gt;
&lt;h1 id=&#34;简介&#34;&gt;&lt;a href=&#34;#简介&#34; class=&#34;headerlink&#34; title=&#34;简介&#34;&gt;&lt;/a&gt;简介&lt;/h1&gt;&lt;h2 id=&#34;ORM-简介&#34;&gt;&lt;a href=&#34;#ORM-简介&#34; class=&#34;headerlink&#34; title=&#34;ORM 简介&#34;&gt;&lt;/a&gt;ORM 简介&lt;/h2&gt;&lt;h3 id=&#34;ORM概念&#34;&gt;&lt;a href=&#34;#ORM概念&#34; class=&#34;headerlink&#34; title=&#34;ORM概念&#34;&gt;&lt;/a&gt;ORM概念&lt;/h3&gt;&lt;p&gt;对象关系映射（Object Relational Mapping，简称ORM）模式是一种为了解决面向对象与关系数据库存在的互不匹配的现象的技术。&lt;/p&gt;
&lt;p&gt;简单的说，ORM是通过使用描述对象和数据库之间映射的元数据，将程序中的对象自动持久化到关系数据库中。&lt;/p&gt;
&lt;p&gt;ORM在业务逻辑层和数据库层之间充当了桥梁的作用。&lt;/p&gt;
&lt;h3 id=&#34;ORM由来&#34;&gt;&lt;a href=&#34;#ORM由来&#34; class=&#34;headerlink&#34; title=&#34;ORM由来&#34;&gt;&lt;/a&gt;ORM由来&lt;/h3&gt;&lt;p&gt;让我们从O&amp;#x2F;R开始。字母O起源于”对象”(Object),而R则来自于”关系”(Relational)。&lt;/p&gt;
&lt;p&gt;几乎所有的软件开发过程中都会涉及到对象和关系数据库。在用户层面和业务逻辑层面，我们是面向对象的。当对象的信息发生变化的时候，我们就需要把对象的信息保存在关系数据库中。&lt;/p&gt;
&lt;p&gt;按照之前的方式来进行开发就会出现程序员会在自己的业务逻辑代码中夹杂很多SQL语句用来增加、读取、修改、删除相关数据，而这些代码通常都是重复的。&lt;/p&gt;
&lt;h3 id=&#34;ORM优势&#34;&gt;&lt;a href=&#34;#ORM优势&#34; class=&#34;headerlink&#34; title=&#34;ORM优势&#34;&gt;&lt;/a&gt;ORM优势&lt;/h3&gt;&lt;p&gt;ORM解决的主要问题是对象和关系的映射。它通常把一个类和一个表一一对应，类的每个实例对应表中的一条记录，类的每个属性对应表中的每个字段。&lt;/p&gt;
&lt;p&gt;ORM提供了对数据库的映射，不用直接编写SQL代码，只需像操作对象一样从数据库操作数据。&lt;/p&gt;
&lt;p&gt;让软件开发人员专注于业务逻辑的处理，提高了开发效率。&lt;/p&gt;
&lt;h3 id=&#34;ORM的劣势&#34;&gt;&lt;a href=&#34;#ORM的劣势&#34; class=&#34;headerlink&#34; title=&#34;ORM的劣势&#34;&gt;&lt;/a&gt;ORM的劣势&lt;/h3&gt;&lt;p&gt;ORM的缺点是会在一定程度上牺牲程序的执行效率。&lt;/p&gt;
&lt;p&gt;ORM用多了SQL语句就不会写了，关系数据库相关技能退化…&lt;/p&gt;
&lt;h3 id=&#34;ORM总结&#34;&gt;&lt;a href=&#34;#ORM总结&#34; class=&#34;headerlink&#34; title=&#34;ORM总结&#34;&gt;&lt;/a&gt;ORM总结&lt;/h3&gt;&lt;p&gt;ORM只是一种工具，工具确实能解决一些重复，简单的劳动。这是不可否认的。&lt;/p&gt;
&lt;p&gt;但我们不能指望某个工具能一劳永逸地解决所有问题，一些特殊问题还是需要特殊处理的。&lt;/p&gt;
&lt;p&gt;但是在整个软件开发过程中需要特殊处理的情况应该都是很少的，否则所谓的工具也就失去了它存在的意义。&lt;/p&gt;
&lt;h2 id=&#34;常见的持久化框架&#34;&gt;&lt;a href=&#34;#常见的持久化框架&#34; class=&#34;headerlink&#34; title=&#34;常见的持久化框架&#34;&gt;&lt;/a&gt;常见的持久化框架&lt;/h2&gt;&lt;p&gt;当前 Java ORM 框架产品有很多，常见的框架有 Hibernate 和 MyBatis，其主要区别如下。&lt;/p&gt;
&lt;h3 id=&#34;Hibernate&#34;&gt;&lt;a href=&#34;#Hibernate&#34; class=&#34;headerlink&#34; title=&#34;Hibernate&#34;&gt;&lt;/a&gt;Hibernate&lt;/h3&gt;&lt;p&gt;Hibernate 框架是一个全表映射的框架。通常开发者只要定义好持久化对象到数据库表的映射关系，就可以通过 Hibernate 框架提供的方法完成持久层操作。&lt;/p&gt;
&lt;p&gt;开发者并不需要熟练地掌握 SQL 语句的编写，Hibernate 框架会根据编制的存储逻辑，自动生成对应的 SQL，并调用 JDBC 接口来执行，所以其开发效率会高于 MyBatis 框架。&lt;/p&gt;
&lt;p&gt;然而 Hibernate 框架自身也存在一些缺点，例如：&lt;/p&gt;
&lt;ul&gt;
&lt;li&gt;多表关联时，对 SQL 查询的支持较差；&lt;/li&gt;
&lt;li&gt;更新数据时，需要发送所有字段；&lt;/li&gt;
&lt;li&gt;不支持存储过程；&lt;/li&gt;
&lt;li&gt;不能通过优化 SQL 来优化性能。&lt;/li&gt;
&lt;/ul&gt;
&lt;p&gt;这些问题导致其只适合在场景不太复杂且对性能要求不高的项目中使用。&lt;/p&gt;
&lt;p&gt;Hibernate 官网：&lt;a href=&#34;http://hibernate.org/&#34;&gt;http://hibernate.org/&lt;/a&gt;&lt;/p&gt;
&lt;h3 id=&#34;MyBatis&#34;&gt;&lt;a href=&#34;#MyBatis&#34; class=&#34;headerlink&#34; title=&#34;MyBatis&#34;&gt;&lt;/a&gt;MyBatis&lt;/h3&gt;&lt;p&gt;MyBatis 框架是一个半自动映射的框架。这里所谓的 “半自动” 是相对于 Hibernate 框架全表映射而言的，MyBatis 框架需要手动匹配提供 POJO、SQL 和映射关系，而 Hibernate 框架只需提供 POJO 和映射关系即可。&lt;/p&gt;
&lt;p&gt;与 Hibernate 框架相比，虽然使用 MyBatis 框架手动编写 SQL 要比使用 Hibernate 框架的工作量大，但 MyBatis 框架可以配置动态 SQL 并优化 SQL、通过配置决定 SQL 的映射规则，以及支持存储过程等。对于一些复杂的和需要优化性能的项目来说，显然使用 MyBatis 框架更加合适。&lt;/p&gt;
&lt;p&gt;MyBatis 框架可应用于需求多变的互联网项目，如电商项目；Hibernate 框架可应用于需求明确、业务固定的项目，如 OA 项目、ERP 项目等。&lt;/p&gt;
&lt;p&gt;学习地址：&lt;a href=&#34;https://mybatis.org/mybatis-3/zh/getting-started.html&#34;&gt;https://mybatis.org/mybatis-3/zh/getting-started.html&lt;/a&gt;&lt;/p&gt;
&lt;h2 id=&#34;MyBatis示例&#34;&gt;&lt;a href=&#34;#MyBatis示例&#34; class=&#34;headerlink&#34; title=&#34;MyBatis示例&#34;&gt;&lt;/a&gt;MyBatis示例&lt;/h2&gt;&lt;blockquote&gt;
&lt;p&gt;项目案例为一个普通的Maven的Java项目&lt;/p&gt;
&lt;/blockquote&gt;
&lt;h3 id=&#34;相关依赖&#34;&gt;&lt;a href=&#34;#相关依赖&#34; class=&#34;headerlink&#34; title=&#34;相关依赖&#34;&gt;&lt;/a&gt;相关依赖&lt;/h3&gt;&lt;figure class=&#34;highlight xml&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;3&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;4&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;5&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;6&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;7&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;8&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;9&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;10&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;11&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;12&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;13&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;14&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;15&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;16&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;17&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;18&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;19&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;code class=&#34;hljs xml&#34;&gt;&lt;span class=&#34;hljs-tag&#34;&gt;&amp;lt;&lt;span class=&#34;hljs-name&#34;&gt;dependencies&lt;/span&gt;&amp;gt;&lt;/span&gt;&lt;br&gt;    &lt;span class=&#34;hljs-comment&#34;&gt;&amp;lt;!-- https://mvnrepository.com/artifact/org.mybatis/mybatis --&amp;gt;&lt;/span&gt;&lt;br&gt;    &lt;span class=&#34;hljs-tag&#34;&gt;&amp;lt;&lt;span class=&#34;hljs-name&#34;&gt;dependency&lt;/span&gt;&amp;gt;&lt;/span&gt;&lt;br&gt;        &lt;span class=&#34;hljs-tag&#34;&gt;&amp;lt;&lt;span class=&#34;hljs-name&#34;&gt;groupId&lt;/span&gt;&amp;gt;&lt;/span&gt;org.mybatis&lt;span class=&#34;hljs-tag&#34;&gt;&amp;lt;/&lt;span class=&#34;hljs-name&#34;&gt;groupId&lt;/span&gt;&amp;gt;&lt;/span&gt;&lt;br&gt;        &lt;span class=&#34;hljs-tag&#34;&gt;&amp;lt;&lt;span class=&#34;hljs-name&#34;&gt;artifactId&lt;/span&gt;&amp;gt;&lt;/span&gt;mybatis&lt;span class=&#34;hljs-tag&#34;&gt;&amp;lt;/&lt;span class=&#34;hljs-name&#34;&gt;artifactId&lt;/span&gt;&amp;gt;&lt;/span&gt;&lt;br&gt;        &lt;span class=&#34;hljs-tag&#34;&gt;&amp;lt;&lt;span class=&#34;hljs-name&#34;&gt;version&lt;/span&gt;&amp;gt;&lt;/span&gt;3.4.6&lt;span class=&#34;hljs-tag&#34;&gt;&amp;lt;/&lt;span class=&#34;hljs-name&#34;&gt;version&lt;/span&gt;&amp;gt;&lt;/span&gt;&lt;br&gt;    &lt;span class=&#34;hljs-tag&#34;&gt;&amp;lt;/&lt;span class=&#34;hljs-name&#34;&gt;dependency&lt;/span&gt;&amp;gt;&lt;/span&gt;&lt;br&gt;    &lt;span class=&#34;hljs-tag&#34;&gt;&amp;lt;&lt;span class=&#34;hljs-name&#34;&gt;dependency&lt;/span&gt;&amp;gt;&lt;/span&gt;&lt;br&gt;        &lt;span class=&#34;hljs-tag&#34;&gt;&amp;lt;&lt;span class=&#34;hljs-name&#34;&gt;groupId&lt;/span&gt;&amp;gt;&lt;/span&gt;org.projectlombok&lt;span class=&#34;hljs-tag&#34;&gt;&amp;lt;/&lt;span class=&#34;hljs-name&#34;&gt;groupId&lt;/span&gt;&amp;gt;&lt;/span&gt;&lt;br&gt;        &lt;span class=&#34;hljs-tag&#34;&gt;&amp;lt;&lt;span class=&#34;hljs-name&#34;&gt;artifactId&lt;/span&gt;&amp;gt;&lt;/span&gt;lombok&lt;span class=&#34;hljs-tag&#34;&gt;&amp;lt;/&lt;span class=&#34;hljs-name&#34;&gt;artifactId&lt;/span&gt;&amp;gt;&lt;/span&gt;&lt;br&gt;        &lt;span class=&#34;hljs-tag&#34;&gt;&amp;lt;&lt;span class=&#34;hljs-name&#34;&gt;version&lt;/span&gt;&amp;gt;&lt;/span&gt;1.18.16&lt;span class=&#34;hljs-tag&#34;&gt;&amp;lt;/&lt;span class=&#34;hljs-name&#34;&gt;version&lt;/span&gt;&amp;gt;&lt;/span&gt;&lt;br&gt;    &lt;span class=&#34;hljs-tag&#34;&gt;&amp;lt;/&lt;span class=&#34;hljs-name&#34;&gt;dependency&lt;/span&gt;&amp;gt;&lt;/span&gt;&lt;br&gt;    &lt;span class=&#34;hljs-tag&#34;&gt;&amp;