&lt;h3 id=&#34;创建SQL&#34;&gt;&lt;a href=&#34;#创建SQL&#34; class=&#34;headerlink&#34; title=&#34;创建SQL&#34;&gt;&lt;/a&gt;创建SQL&lt;/h3&gt;&lt;figure class=&#34;highlight plaintext&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;3&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;4&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;5&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;6&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;7&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;8&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;9&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;10&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;11&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;12&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;13&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;14&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;code class=&#34;hljs mysql&#34;&gt;CREATE TABLE `inter_employee` (&lt;br&gt;  `id` varchar(32) NOT NULL COMMENT &amp;#x27;id&amp;#x27;,&lt;br&gt;  `name` varchar(32) NOT NULL COMMENT &amp;#x27;姓名&amp;#x27;,&lt;br&gt;  `email` varchar(128) NOT NULL COMMENT &amp;#x27;邮箱&amp;#x27;,&lt;br&gt;  `sex` char(1) NOT NULL COMMENT &amp;#x27;性别 0-男；1-女；2-未知&amp;#x27;,&lt;br&gt;  `dept_id` bigint NOT NULL COMMENT &amp;#x27;所属部门id&amp;#x27;,&lt;br&gt;  `org_id` varchar(32) NOT NULL COMMENT &amp;#x27;所属机构id&amp;#x27;,&lt;br&gt;  `status` char(1) NOT NULL COMMENT &amp;#x27;状态 0-无效；1-有效&amp;#x27;,&lt;br&gt;  `created_by` varchar(32) DEFAULT NULL COMMENT &amp;#x27;创建人&amp;#x27;,&lt;br&gt;  `created_time` datetime DEFAULT NULL COMMENT &amp;#x27;创建时间&amp;#x27;,&lt;br&gt;  `updated_by` varchar(32) DEFAULT NULL COMMENT &amp;#x27;更新人&amp;#x27;,&lt;br&gt;  `updated_time` datetime DEFAULT