&lt;h3 id=&#34;注入数据&#34;&gt;&lt;a href=&#34;#注入数据&#34; class=&#34;headerlink&#34; title=&#34;注入数据&#34;&gt;&lt;/a&gt;注入数据&lt;/h3&gt;&lt;figure class=&#34;highlight sql&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;3&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;4&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;5&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;6&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;code class=&#34;hljs sql&#34;&gt;&lt;span class=&#34;hljs-keyword&#34;&gt;INSERT&lt;/span&gt; &lt;span class=&#34;hljs-keyword&#34;&gt;INTO&lt;/span&gt; `inter_employee`(`id`, `name`, `email`, `sex`, `dept_id`, `org_id`, `status`, `created_by`, `created_time`, `updated_by`, `updated_time`) &lt;span class=&#34;hljs-keyword&#34;&gt;VALUES&lt;/span&gt; (&lt;span class=&#34;hljs-string&#34;&gt;&amp;#x27;a999b75045ac4c51a537b4a2f3631da8&amp;#x27;&lt;/span&gt;, &lt;span class=&#34;hljs-string&#34;&gt;&amp;#x27;will——5&amp;#x27;&lt;/span&gt;, &lt;span class=&#34;hljs-string&#34;&gt;&amp;#x27;1@qqc.om&amp;#x27;&lt;/span&gt;, &lt;span class=&#34;hljs-string&#34;&gt;&amp;#x27;1&amp;#x27;&lt;/span&gt;, &lt;span class=&#34;hljs-number&#34;&gt;1001&lt;/span&gt;, &lt;span class=&#34;hljs-string&#34;&gt;&amp;#x27;a007fd3f3d4242b880d9eae31514ba1f&amp;#x27;&lt;/span&gt;, &lt;span class=&#34;hljs-string&#34;&gt;&amp;#x27;1&amp;#x27;&lt;/span&gt;, &lt;span class=&#34;hljs-string&#34;&gt;&amp;#x27;admin&amp;#x27;&lt;/span&gt;, &lt;span class=&#34;hljs-string&#34;&gt;&amp;#x27;2020-06-03 22:11:26&amp;#x27;&lt;/span&gt;, &lt;span class=&#34;hljs-keyword&#34;&gt;NULL&lt;/span&gt;, &lt;span class=&#34;hljs-string&#34;&gt;&amp;#x27;2020-06-03 22:11:26&amp;#x27;&lt;/span&gt;);&lt;br&gt;&lt;span class=&#34;hljs-keyword&#34;&gt;INSERT&lt;/span&gt; &lt;s