&lt;h3 id=&#34;Controller-1&#34;&gt;&lt;a href=&#34;#Controller-1&#34; class=&#34;headerlink&#34; title=&#34;Controller&#34;&gt;&lt;/a&gt;Controller&lt;/h3&gt;&lt;figure class=&#34;highlight java&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;3&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;4&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;5&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;6&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;7&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;8&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;9&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;10&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;11&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;12&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;13&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;14&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;15&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;16&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;17&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;18&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;19&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;20&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;21&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;22&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;23&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;24&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;25&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;26&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;27&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;28&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;29&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;30&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;31&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;32&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;33&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;34&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;35&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;36&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;37&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;38&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;39&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;40&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;41&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;42&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;43&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;44&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;45&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;46&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;47&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;48&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;49&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;50&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;51&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;52&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;53&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;54&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;code class=&#34;hljs java&#34;&gt;&lt;span class=&#34;hljs-comment&#34;&gt;/**&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;hljs-comment&#34;&gt; * &lt;span class=&#34;hljs-doctag&#34;&gt;@ClassName&lt;/span&gt; OrderController&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;hljs-comment&#34;&gt; * &lt;span class=&#34;hljs-doctag&#34;&gt;@Description&lt;/span&gt; TODO&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;hljs-comment&#34;&gt; * &lt;span class=&#34;hljs-doctag&#34;&gt;@Author&lt;/span&gt; will&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;hljs-comment&#34;&gt; * &lt;span class=&#34;hljs-doctag&#34;&gt;@Date&lt;/span&gt; 2021/6/20 20:35&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;hljs-comment&#34;&gt; */&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;hljs-meta&#34;&gt;@RestController&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;hljs-meta&#34;&gt;@Slf4j&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;hljs-meta&#34;&gt;@RequestMapping(&amp;quot;/order&amp;quot;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;hljs-keyword&#34;&gt;public&lt;/span&gt; &lt;span class=&#34;hljs-keyword&#34;&gt;class&lt;/span&gt; &lt;span class=&#34;hljs-title class_&#34;&gt;OrderController&lt;/span&gt; &amp;#123;&lt;br&gt;&lt;br&gt;    &lt;span class=&#34;hljs-keyword&#34;&gt;private&lt;/span&gt; &lt;span class=&#34;hljs-keyword&#34;&gt;static&lt;/span&gt; &lt;span class=&#34;hljs-keyword&#34;&gt;final&lt;/span&gt; &lt;span class=&#34;hljs-type&#34;&gt;String&lt;/span&gt; &lt;span class=&#34;hljs-variable&#34;&gt;REST_TEMPLATE&lt;/span&gt; &lt;span class=&#34;hljs-operator&#34;&gt;=&lt;/span&gt; &lt;span class=&#34;hljs-string&#34;&gt;&amp;quot;restTemplate&amp;quot;&lt;/span&gt;;&lt;br&gt;&lt;br&gt;    &lt;span class=&#34;hljs-meta&#34;&gt;@Autowired&lt;/span&gt;&lt;br&gt;    &lt;span class=&#34;hljs-keyword&#34;&gt;private&lt;/span&gt; OrderService orderService;&lt;br&gt;    &lt;span class=&#34;hljs-meta&#34;&gt;@Autowired&lt;/span&gt;&lt;br&gt;    &lt;span class=&#34;hljs-keyword&#34;&gt;private&lt;/span&gt; RestTemplate restTemplate;&lt;br&gt;    &lt;span class=&#34;hljs-meta&#34;&gt;@Autowired&lt;/span&gt;&lt;br&gt;    &lt;span class=&#34;hljs-keyword&#34;&gt;private&lt;/span&gt; DiscoveryClient discoveryClient;&lt;br&gt;&lt;br&gt;    &lt;span class=&#34;hljs-comment&#34;&gt;/**&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;hljs-comment&#34;&gt;     * 保存指定产品订单&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;hljs-comment&#34;&gt;     * &lt;span class=&#34;hljs-doctag&#34;&gt;@param&lt;/span&gt; pId&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;hljs-comment&#34;&gt;     * &lt;span class=&#34;hljs-doctag&#34;&gt;@return&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;hljs-comment&#34;&gt;     */&lt;/span&gt;&lt;br&gt;    &lt;span class=&#34;hljs-meta&#34;&gt;@GetMapping(&amp;quot;/save/&amp;#123;pId&amp;#125;&amp;quot;)&lt;/span&gt;&lt;br&gt;    &lt;span class=&#34;hljs-keyword&#34;&gt;public&lt;/span&gt; Order &lt;span class=&#34;hljs-title function_&#34;&gt;order&lt;/span&gt;&lt;span class=&#34;hljs-params&#34;&gt;(&lt;span class=&#34;hljs-meta&#34;&gt;@PathVariable(&amp;quot;pId&amp;quot;)&lt;/span&gt; Integer pId, &lt;span class=&#34;hljs-meta&#34;&gt;@RequestParam(name = &amp;quot;queryType&amp;quot;)&lt;/span&gt; String queryType)&lt;/span&gt; &amp;#123;&lt;br&gt;        log.info(&lt;span class=&#34;hljs-string&#34;&gt;&amp;quot;&amp;gt;&amp;gt;客户下单，这时候要调用商品微服务查询商品信息&amp;quot;&lt;/span&gt;);&lt;br&gt;        &lt;span class=&#34;hljs-type&#34;&gt;Product&lt;/span&gt; &lt;span class=&#34;hljs-variable&#34;&gt;product&lt;/span&gt; &lt;span class=&#34;hljs-operator&#34;&gt;=&lt;/span&gt; &lt;span class=&#34;hljs-literal&#34;&gt;null&lt;/span&gt;;&lt;br&gt;        &lt;span class=&#34;hljs-comment&#34;&gt;// 通过restTemplate调用商品微服务&lt;/span&gt;&lt;br&gt;        &lt;span class=&#34;hljs-keyword&#34;&gt;if&lt;/span&gt; (REST_TEMPLATE.equals(queryType)) &amp;#123;&lt;br&gt;            product = queryProductByRestTemplate(pId);&lt;br&gt;        &amp;#125; &lt;br&gt;        log.info(&lt;span class=&#34;hljs-string&#34;&gt;&amp;quot;&amp;gt;&amp;gt;商品信息,查询结果:&amp;quot;&lt;/span&gt; + JSON.toJSONString(product));&lt;br&gt;        &lt;span class=&#34;hljs-type&#34;&gt;Order&lt;/span&gt; &lt;span class=&#34;hljs-variable&#34;&gt;order&lt;/span&gt; &lt;span class=&#34;hljs-operator&#34;&gt;=&lt;/span&gt; &lt;span class=&#34;hljs-keyword&#34;&gt;new&lt;/span&gt; &lt;span class=&#34;hljs-title class_&#34;&gt;Order&lt;/span&gt;();&lt;br&gt;        order.setUId(&lt;span class=&#34;hljs-number&#34;&gt;1&lt;/span&gt;);&lt;br&gt;        order.setUserName(&lt;span class=&#34;hljs-string&#34;&gt;&amp;quot;测试用户&amp;quot;&lt;/span&gt;);&lt;br&gt;        order.setPId(product.getPId());&lt;br&gt;        order.setPName(product.getPName());&lt;br&gt;        order.setPPrice(product.getPPrice());&lt;br&gt;        order.setNumber(&lt;span class=&#34;hljs-number&#34;&gt;1&lt;/span&gt;);&lt;br&gt;        orderService.saveOrder(order);&lt;br&gt;        &lt;span class=&#34;hljs-keyword&#34;&gt;return&lt;/span&gt; order;&lt;br&gt;    &amp;#125;&lt;br&gt;&lt;br&gt;    &lt;span class=&#34;hljs-comment&#34;&gt;/**&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;hljs-comment&#34;&gt;     * RestTemplate风格查询&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;hljs-comment&#34;&gt;     * &lt;span class=&#34;hljs-doctag&#34;&gt;@param&lt;/span&gt; pId    产品id&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;hljs-comment&#34;&gt;     * &lt;span class=&#34;hljs-doctag&#34;&gt;@return&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;hljs-comment&#34;&gt;     */&lt;/span&gt;&lt;br&gt;    &lt;span class=&#34;hljs-keyword&#34;&gt;private&lt;/span&gt; Product &lt;span class=&#34;hljs-title function_&#34;&gt;queryProductByRestTemplate&lt;/span&gt;&lt;span class=&#34;hljs-params&#34;&gt;(Integer pId)&lt;/span&gt; &amp;#123;&lt;br&gt;        &lt;span class=&#34;hljs-keyword&#34;&gt;return&lt;/span&gt; restTemplate.getForObject(&lt;span class=&#34;hljs-string&#34;&gt;&amp;quot;http://localhost:8081/product/info/&amp;quot;&lt;/span&gt; + pId, Product.class);&lt;br&gt;    &amp;#125;&lt;br&gt;&amp;#125;&lt;br&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;

 &lt;h1 id=&#34;微服务介绍&#34;&gt;&lt;a href=&#34;#微服务介绍&#34; class=&#34;headerlink&#34; title=&#34;微服务介绍&#34;&gt;&lt;/a&gt;微服务介绍&lt;/h1&gt;&lt;h2 id=&#34;微服务架构介绍&#34;&gt;&lt;a href=&#34;#微服务架构介绍&#34; class=&#34;headerlink&#34; title=&#34;微服务架构介绍&#34;&gt;&lt;/a&gt;微服务架构介绍&lt;/h2&gt;&lt;p&gt;微服务架构， 简单的说就是将单体应用进一步拆分，拆分成更小的服务，每个服务都是一个可以独立运行的项目。&lt;/p&gt;
&lt;h2 id=&#34;微服务架构的常见问题&#34;&gt;&lt;a href=&#34;#微服务架构的常见问题&#34; class=&#34;headerlink&#34; title=&#34;微服务架构的常见问题&#34;&gt;&lt;/a&gt;微服务架构的常见问题&lt;/h2&gt;&lt;p&gt;一旦采用微服务系统架构，就势必会遇到这样几个问题：&lt;/p&gt;
&lt;ul&gt;
&lt;li&gt;这么多小服务，如何管理他们？(服务治理 注册中心[服务注册 发现 剔除])&lt;/li&gt;
&lt;li&gt;这么多小服务，他们之间如何通讯？(restful rpc)&lt;/li&gt;
&lt;li&gt;这么多小服务，客户端怎么访问他们？(网关)&lt;/li&gt;
&lt;li&gt;这么多小服务，一旦出现问题了，应该如何自处理？(容错)&lt;/li&gt;
&lt;li&gt;这么多小服务，一旦出现问题了，应该如何排错? (链路追踪)&lt;/li&gt;
&lt;/ul&gt;
&lt;p&gt;对于上面的问题，是任何一个微服务设计者都不能绕过去的，因此大部分的微服务产品都针对每一个问题提供了相应的组件来解决它们。&lt;/p&gt;
&lt;p&gt;&lt;img src=&#34;https://gitee.com/yangxiangnanWorkSpace/uploadImages/raw/master/static/20200710114705.jpg&#34;&gt;&lt;/p&gt;
&lt;h2 id=&#34;微服务架构的常见概念&#34;&gt;&lt;a href=&#34;#微服务架构的常见概念&#34; class=&#34;headerlink&#34; title=&#34;微服务架构的常见概念&#34;&gt;&lt;/a&gt;微服务架构的常见概念&lt;/h2&gt;&lt;h3 id=&#34;服务治理&#34;&gt;&lt;a href=&#34;#服务治理&#34; class=&#34;headerlink&#34; title=&#34;服务治理&#34;&gt;&lt;/a&gt;服务治理&lt;/h3&gt;&lt;p&gt;服务治理就是进行服务的自动化管理，其核心是服务的自动注册与发现。&lt;/p&gt;
&lt;ul&gt;
&lt;li&gt;服务注册：服务实例将自身服务信息注册到注册中心。&lt;/li&gt;
&lt;li&gt;服务发现：服务实例通过注册中心，获取到注册到其中的服务实例的信息，通过这些信息去请求它们提供的服务。&lt;/li&gt;
&lt;li&gt;服务剔除：服务注册中心将出问题的服务自动剔除到可用列表之外，使其不会被调用到。&lt;/li&gt;
&lt;/ul&gt;
&lt;h3 id=&#34;服务调用&#34;&gt;&lt;a href=&#34;#服务调用&#34; class=&#34;headerlink&#34; title=&#34;服务调用&#34;&gt;&lt;/a&gt;服务调用&lt;/h3&gt;&lt;p&gt;在微服务架构中，通常存在多个服务之间的远程调用的需求。目前主流的远程调用技术有基于HTTP的RESTful接口以及基于TCP的RPC协议。&lt;/p&gt;
&lt;ul&gt;
&lt;li&gt;&lt;p&gt;REST(Representational State Transfer)这是一种HTTP调用的格式，更标准，更通用，无论哪种语言都支持http协议&lt;/p&gt;
&lt;/li&gt;
&lt;li&gt;&lt;p&gt;RPC（Remote Promote Call）一种进程间通信方式。允许像调用本地服务一样调用远程服务。RPC框架的主要目标就是让远程服务调用更简单、透明。RPC框架负责屏蔽底层的传输方式、序列化方式和通信细节。开发人员在使用的时候只需要了解谁在什么位置提供了什么样的远程服务接口即可，并不需要关心底层通信细节和调用过程。&lt;/p&gt;
&lt;p&gt;&lt;strong&gt;区别与联系&lt;/strong&gt;&lt;/p&gt;
&lt;table&gt;
&lt;thead&gt;
&lt;tr&gt;
&lt;th&gt;比较项&lt;/th&gt;
&lt;th&gt;RESTful&lt;/th&gt;
&lt;th&gt;RPC&lt;/th&gt;
&lt;/tr&gt;
&lt;/thead&gt;
&lt;tbody&gt;&lt;tr&gt;
&lt;td&gt;通讯协议&lt;/td&gt;
&lt;td&gt;HTTP&lt;/td&gt;
&lt;td&gt;一般使用TCP&lt;/td&gt;
&lt;/tr&gt;
&lt;tr&gt;
&lt;td&gt;性能&lt;/td&gt;
&lt;td&gt;略低&lt;/td&gt;
&lt;td&gt;较高&lt;/td&gt;
&lt;/tr&gt;
&lt;tr&gt;
&lt;td&gt;灵活度&lt;/td&gt;
&lt;td&gt;高&lt;/td&gt;
&lt;td&gt;低&lt;/td&gt;
&lt;/tr&gt;
&lt;tr&gt;
&lt;td&gt;应用&lt;/td&gt;
&lt;td&gt;微服务架构&lt;/td&gt;
&lt;td&gt;SOA架构&lt;/td&gt;
&lt;/tr&gt;
&lt;/tbody&gt;&lt;/table&gt;
&lt;/li&gt;
&lt;/ul&gt;
&lt;h3 id=&#34;服务网关&#34;&gt;&lt;a href=&#34;#服务网关&#34; class=&#34;headerlink&#34; title=&#34;服务网关&#34;&gt;&lt;/a&gt;服务网关&lt;/h3&gt;&lt;p&gt;随着微服务的不断增多，不同的微服务一般会有不同的网络地址，而外部客户端可能需要调用多个服务的接口才能完成一个业务需求，如果让客户端直接与各个微服务通信可能出现：&lt;/p&gt;
&lt;ul&gt;
&lt;li&gt;客户端需要调用不同的url地址，增加难度&lt;/li&gt;
&lt;li&gt;在一定的场景下，存在跨域请求的问题&lt;/li&gt;
&lt;li&gt;每个微服务都需要进行单独的身份认证&lt;/li&gt;
&lt;/ul&gt;
&lt;p&gt;针对这些问题，API网关顺势而生。&lt;br&gt;API网关直面意思是将所有API调用统一接入到API网关层，由网关层统一接入和输出。一个网关的基本功能有：统一接入、安全防护、协议适配、流量管控、长短链接支持、容错能力。有了网关之后，各个API服务提供团队可以专注于自己的的业务逻辑处理，而API网关更专注于安全、流量、路由等问题。&lt;/p&gt;
&lt;h3 id=&#34;服务容错&#34;&gt;&lt;a href=&#34;#服务容错&#34; class=&#34;headerlink&#34; title=&#34;服务容错&#34;&gt;&lt;/a&gt;服务容错&lt;/h3&gt;&lt;p&gt;在微服务当中，一个请求经常会涉及到调用几个服务，如果其中某个服务不可用，没有做服务容错的话，极有可能会造成一连串的服务不可用，这就是雪崩效应。&lt;br&gt;我们没法预防雪崩效应的发生，只能尽可能去做好容错。服务容错的三个核心思想是：&lt;/p&gt;
&lt;ul&gt;
&lt;li&gt;不被外界环境影响&lt;/li&gt;
&lt;li&gt;不被上游请求压垮&lt;/li&gt;
&lt;li&gt;不被下游响应拖垮&lt;/li&gt;
&lt;/ul&gt;
&lt;p&gt;&lt;img src=&#34;https://gitee.com/yangxiangnanWorkSpace/uploadImages/raw/master/static/20200710114704.png&#34;&gt;&lt;/p&gt;
&lt;h3 id=&#34;链路追踪&#34;&gt;&lt;a href=&#34;#链路追踪&#34; class=&#34;headerlink&#34; title=&#34;链路追踪&#34;&gt;&lt;/a&gt;链路追踪&lt;/h3&gt;&lt;p&gt;随着微服务架构的流行，服务按照不同的维度进行拆分，一次请求往往需要涉及到多个服务。互联网应用构建在不同的软件模块集上，这些软件模块，有可能是由不同的团队开发、可能使用不同的编程语言来实现、有可能布在了几千台服务器，横跨多个不同的数据中心。因此，就需要对一次请求涉及的多个服务链路进行日志记录，性能监控即链路追踪。&lt;/p&gt;
&lt;p&gt;&lt;img src=&#34;https://gitee.com/yangxiangnanWorkSpace/uploadImages/raw/master/static/20200710141312.png&#34;&gt;&lt;/p&gt;
&lt;h2 id=&#34;微服务架构的常见解决方案&#34;&gt;&lt;a href=&#34;#微服务架构的常见解决方案&#34; class=&#34;headerlink&#34; title=&#34;微服务架构的常见解决方案&#34;&gt;&lt;/a&gt;微服务架构的常见解决方案&lt;/h2&gt;&lt;h3 id=&#34;ServiceComb&#34;&gt;&lt;a href=&#34;#ServiceComb&#34; class=&#34;headerlink&#34; title=&#34;ServiceComb&#34;&gt;&lt;/a&gt;ServiceComb&lt;/h3&gt;&lt;p&gt;&lt;img src=&#34;https://gitee.com/yangxiangnanWorkSpace/uploadImages/raw/master/static/20200710141736.png&#34; alt=&#34;image-20200710141731679&#34;&gt;&lt;/p&gt;
&lt;p&gt;Apache ServiceComb，前身是华为云的微服务引擎 CSE (Cloud Service Engine) 云服务，是全球首个Apache微服务顶级项目。它提供了一站式的微服务开源解决方案，致力于帮助企业、用户和开发者将企业应用轻松微服务化上云，并实现对微服务应用的高效运维管理。&lt;/p&gt;
&lt;h3 id=&#34;SpringCloud&#34;&gt;&lt;a href=&#34;#SpringCloud&#34; class=&#34;headerlink&#34; title=&#34;SpringCloud&#34;&gt;&lt;/a&gt;SpringCloud&lt;/h3&gt;&lt;p&gt;&lt;img src=&#34;https://gitee.com/yangxiangnanWorkSpace/uploadImages/raw/master/static/20200710143511.png&#34; alt=&#34;image-20200710143509327&#34;&gt;&lt;/p&gt;
&lt;p&gt;Spring Cloud是一系列框架的集合。它利用Spring Boot的开发便利性巧妙地简化了分布式系统基础设施的开发，如服务发现注册、配置中心、消息总线、负载均衡、断路器、数据监控等，都可以用Spring Boot的开发风格做到一键启动和部署。&lt;br&gt;Spring Cloud并没有重复制造轮子，它只是将目前各家公司开发的比较成熟、经得起实际考验的服务框架组合起来，通过Spring Boot风格进行再封装屏蔽掉了复杂的配置和实现原理，最终给开发者留出了一套简单易懂、易部署和易维护的分布式系统开发工具包。&lt;/p&gt;
&lt;h3 id=&#34;SpringCloud-Alibaba&#34;&gt;&lt;a href=&#34;#SpringCloud-Alibaba&#34; class=&#34;headerlink&#34; title=&#34;SpringCloud Alibaba&#34;&gt;&lt;/a&gt;SpringCloud Alibaba&lt;/h3&gt;&lt;p&gt;&lt;img src=&#34;https://gitee.com/yangxiangnanWorkSpace/uploadImages/raw/master/static/20200710143714.png&#34; alt=&#34;image-20200710143710723&#34;&gt;&lt;/p&gt;
&lt;p&gt;Spring Cloud Alibaba 致力于提供微服务开发的一站式解决方案。此项目包含开发分布式应用微服务的必需组件，方便开发者通过 Spring Cloud 编程模型轻松使用这些组件来开发分布式应用服务。&lt;/p&gt;
&lt;h2 id=&#34;SpringCloud-Alibaba介绍&#34;&gt;&lt;a href=&#34;#SpringCloud-Alibaba介绍&#34; class=&#34;headerlink&#34; title=&#34;SpringCloud Alibaba介绍&#34;&gt;&lt;/a&gt;SpringCloud Alibaba介绍&lt;/h2&gt;&lt;p&gt;Spring Cloud Alibaba 致力于提供微服务开发的一站式解决方案。此项目包含开发分布式应用微服务的必需组件，方便开发者通过 Spring Cloud 编程模型轻松使用这些组件来开发分布式应用服务。&lt;br&gt;依托 Spring Cloud Alibaba，您只需要添加一些注解和少量配置，就可以将 Spring Cloud 应用接入阿里微服务解决方案，通过阿里中间件来迅速搭建分布式应用系统。&lt;/p&gt;
&lt;h3 id=&#34;主要功能&#34;&gt;&lt;a href=&#34;#主要功能&#34; class=&#34;headerlink&#34; title=&#34;主要功能&#34;&gt;&lt;/a&gt;主要功能&lt;/h3&gt;&lt;ul&gt;
&lt;li&gt;&lt;strong&gt;服务限流降级：默认支持 WebServlet、WebFlux， OpenFeign、RestTemplate、Spring CloudGateway， Zuul， Dubbo 和 RocketMQ 限流降级功能的接入，可以在运行时通过控制台实时修改限流降级规则，还支持查看限流降级 Metrics 监控。&lt;/strong&gt;&lt;/li&gt;
&lt;li&gt;&lt;strong&gt;服务注册与发现：适配 Spring Cloud 服务注册与发现标准，默认集成了 Ribbon 的支持。&lt;/strong&gt;&lt;/li&gt;
&lt;li&gt;&lt;strong&gt;分布式配置管理：支持分布式系统中的外部化配置，配置更改时自动刷新。&lt;/strong&gt;&lt;/li&gt;
&lt;li&gt;&lt;strong&gt;消息驱动能力：基于 Spring Cloud Stream 为微服务应用构建消息驱动能力。&lt;/strong&gt;&lt;/li&gt;
&lt;li&gt;&lt;strong&gt;分布式事务：使用 @GlobalTransactional 注解， 高效并且对业务零侵入地解决分布式事务问题。&lt;/strong&gt;&lt;/li&gt;
&lt;li&gt;&lt;strong&gt;阿里云对象存储：阿里云提供的海量、安全、低成本、高可靠的云存储服务。支持在任何应用、任何时间、任何地点存储和访问任意类型的数据。&lt;/strong&gt;&lt;/li&gt;
&lt;li&gt;&lt;strong&gt;分布式任务调度：提供秒级、精准、高可靠、高可用的定时（基于 Cron 表达式）任务调度服务。同时提供分布式的任务执行模型，如网格任务。网格任务支持海量子任务均匀分配到所有Worker（schedulerx-client）上执行。&lt;/strong&gt;&lt;/li&gt;
&lt;li&gt;&lt;strong&gt;阿里云短信服务：覆盖全球的短信服务，友好、高效、智能的互联化通讯能力，帮助企业迅速搭建客户触达通道。&lt;/strong&gt;&lt;/li&gt;
&lt;/ul&gt;
&lt;h3 id=&#34;组件&#34;&gt;&lt;a href=&#34;#组件&#34; class=&#34;headerlink&#34; title=&#34;组件&#34;&gt;&lt;/a&gt;组件&lt;/h3&gt;&lt;ul&gt;
&lt;li&gt;&lt;strong&gt;Sentinel：把流量作为切入点，从流量控制、熔断降级、系统负载保护等多个维度保护服务的稳定性。&lt;/strong&gt;&lt;/li&gt;
&lt;li&gt;&lt;strong&gt;Nacos：一个更易于构建云原生应用的动态服务发现、配置管理和服务管理平台。&lt;/strong&gt;&lt;/li&gt;
&lt;li&gt;&lt;strong&gt;RocketMQ：一款开源的分布式消息系统，基于高可用分布式集群技术，提供低延时的、高可靠的消息发布与订阅服务。&lt;/strong&gt;&lt;/li&gt;
&lt;li&gt;&lt;strong&gt;Dubbo：Apache Dubbo™ 是一款高性能 Java RPC 框架。&lt;/strong&gt;&lt;/li&gt;
&lt;li&gt;&lt;strong&gt;Seata：阿里巴巴开源产品，一个易于使用的高性能微服务分布式事务解决方案。&lt;/strong&gt;&lt;/li&gt;
&lt;li&gt;&lt;strong&gt;Alibaba Cloud ACM：一款在分布式架构环境中对应用配置进行集中管理和推送的应用配置中心产品。&lt;/strong&gt;&lt;/li&gt;
&lt;li&gt;&lt;strong&gt;Alibaba Cloud OSS: 阿里云对象存储服务（Object Storage Service，简称 OSS），是阿里云提供的海量、安全、低成本、高可靠的云存储服务。您可以在任何应用、任何时间、任何地点存储和访问任意类型的数据。&lt;/strong&gt;&lt;/li&gt;
&lt;li&gt;&lt;strong&gt;Alibaba Cloud SchedulerX: 阿里中间件团队开发的一款分布式任务调度产品，提供秒级、精准、高可靠、高可用的定时（基于 Cron 表达式）任务调度服务。&lt;/strong&gt;&lt;/li&gt;
&lt;li&gt;&lt;strong&gt;Alibaba Cloud SMS: 覆盖全球的短信服务，友好、高效、智能的互联化通讯能力，帮助企业迅速搭建客户触达通道。&lt;/strong&gt;&lt;/li&gt;
&lt;/ul&gt;
&lt;blockquote&gt;
&lt;p&gt;（注：后四项都是收费产品）&lt;/p&gt;
&lt;/blockquote&gt;

 &lt;h1 id=&#34;前言&#34;&gt;&lt;a href=&#34;#前言&#34; class=&#34;headerlink&#34; title=&#34;前言&#34;&gt;&lt;/a&gt;前言&lt;/h1&gt;&lt;blockquote&gt;
&lt;p&gt;最近博猪在做一些风险测算相关的工作，大体业务流程就是业务人员通过Excel录入数据后，后台通过默认的风险因子计算后，批量入库，同时会把业务人员导入的测算数据的Excel上传,由于这块操作比较多，但是因为后台使用的ORM框架是MybatisPlus，里面提供的一些API还是挺便捷的，但是同步通过输入测验发现一个问题，下面就以一个案例进入一下今天的主题，细扒一下MyBatisPlus的批量插入操作。&lt;/p&gt;
&lt;/blockquote&gt;
&lt;h1 id=&#34;rewriteBatchedStatements参数&#34;&gt;&lt;a href=&#34;#rewriteBatchedStatements参数&#34; class=&#34;headerlink&#34; title=&#34;rewriteBatchedStatements参数&#34;&gt;&lt;/a&gt;rewriteBatchedStatements参数&lt;/h1&gt;&lt;p&gt;MySQL的JDBC连接的url中要加rewriteBatchedStatements参数，并保证5.1.13以上版本的驱动，才能实现高性能的批量插入。MySQL JDBC驱动在默认情况下会无视executeBatch()语句，把我们期望批量执行的一组sql语句拆散，一条一条地发给MySQL数据库，批量插入实际上是单条插入，直接造成较低的性能。只有把rewriteBatchedStatements参数置为true, 驱动才会帮你批量执行SQL，另外这个选项对INSERT&amp;#x2F;UPDATE&amp;#x2F;DELETE都有效&lt;/p&gt;
&lt;p&gt;添加&lt;code&gt;rewriteBatchedStatements=true&lt;/code&gt;这个参数后的执行速度比较：&lt;/p&gt;
&lt;figure class=&#34;highlight shell&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;code class=&#34;hljs shell&#34;&gt;jdbc:mysql://数据库地址/数据库名?useUnicode=true&amp;amp;characterEncoding=UTF8&amp;amp;allowMultiQueries=true&amp;amp;rewriteBatchedStatements=true&lt;br&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;

&lt;h1 id=&#34;测试环境介绍&#34;&gt;&lt;a href=&#34;#测试环境介绍&#34; class=&#34;headerlink&#34; title=&#34;测试环境介绍&#34;&gt;&lt;/a&gt;测试环境介绍&lt;/h1&gt;&lt;blockquote&gt;
&lt;p&gt;测试代码在本地执行，数据库使用本地虚拟机。&lt;/p&gt;
&lt;/blockquote&gt;
&lt;h2 id=&#34;宿主环境&#34;&gt;&lt;a href=&#34;#宿主环境&#34; class=&#34;headerlink&#34; title=&#34;宿主环境&#34;&gt;&lt;/a&gt;宿主环境&lt;/h2&gt;&lt;ul&gt;
&lt;li&gt;物理机处理为AMD Ryzen 9 5900HX with Radeon Graphics           3.30 GHz   48G运行内存  WIN11(OS)&lt;/li&gt;
&lt;li&gt;MySQL为8.0.30，虚拟机（4G运存,20G内存)&lt;/li&gt;
&lt;li&gt;jdk版本为1.8&lt;/li&gt;
&lt;li&gt;日志级别调整到info或者warn级别，减少日志打印性能的消耗&lt;/li&gt;
&lt;/ul&gt;
&lt;h2 id=&#34;代码&#34;&gt;&lt;a href=&#34;#代码&#34; class=&#34;headerlink&#34; title=&#34;代码&#34;&gt;&lt;/a&gt;代码&lt;/h2&gt;&lt;h3 id=&#34;表结构&#34;&gt;&lt;a href=&#34;#表结构&#34; class=&#34;headerlink&#34; title=&#34;表结构&#34;&gt;&lt;/a&gt;表结构&lt;/h3&gt;&lt;figure class=&#34;highlight plaintext&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;3&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;4&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;5&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;6&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;7&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;code class=&#34;hljs mysql&#34;&gt;CREATE TABLE `user` (&lt;br&gt;  `id` bigint NOT NULL COMMENT &amp;#x27;主键ID&amp;#x27;,&lt;br&gt;  `name` varchar(30) DEFAULT NULL COMMENT &amp;#x27;姓名&amp;#x27;,&lt;br&gt;  `age` int DEFAULT NULL COMMENT &amp;#x27;年龄&amp;#x27;,&lt;br&gt;  `email` varchar(50) DEFAULT NULL COMMENT &amp;#x27;邮箱&amp;#x27;,&lt;br&gt;  PRIMARY KEY (`id`)&lt;br&gt;) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;&lt;br&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;

&lt;h3 id=&#34;测试代码&#34;&gt;&lt;a href=&#34;#测试代码&#34; class=&#34;headerlink&#34; title=&#34;测试代码&#34;&gt;&lt;/a&gt;测试代码&lt;/h3&gt;&lt;figure class=&#34;highlight java&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;3&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;4&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;5&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;6&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;7&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;8&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;9&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;10&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;11&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;12&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;13&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;14&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;15&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;16&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;17&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;18&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;19&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;20&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;21&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;22&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;23&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;24&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;25&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;26&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;27&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;28&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;29&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;30&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#